mapper.insert:
INSERT INTO car_information (
    offer_report_number,      -- 제안서 번호
    vin_number,               -- 차대 번호
    description,              -- 차량 설명
    cate_code,                -- 소분류 코드
    sold_status               -- 판매 상태
) VALUES (
             #{offerReportNumber}, #{vinNumber}, #{description}, #{smallCateCode}, #{soldStatus}
         )

mapper.update:
UPDATE car_information
SET is_deleted = TRUE        -- 차량 삭제 플래그 설정
WHERE car_info_id = #{carInfoId}

